import React, { useState } from 'react';
import { PopularData } from './Totaldata';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart,faSquareCaretRight } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';

const Popular = () => {
  const [filter, setFilter] = useState('All');
  // const [likedItems, setLikedItems] = useState(PopularData.map(item => ({ ...item, liked: false })));

  // const handleLike = (id) => {
  //   const newLikedItems = likedItems.map(item =>
  //     item.id === id ? { ...item, liked: !item.liked } : item
  //   );
  //   setLikedItems(newLikedItems);
  //   console.log(newLikedItems.find(item => item.id === id));
  // };

  const filteredData = PopularData.filter(item => {
    if (filter === 'All') return true;
    if (filter === '2 BHK' && item.title.includes('2BHK')) return true;
    if (filter === '3 BHK' && item.title.includes('3BHK')) return true;
    return false;
  });

  return (
    <div className='container mt-5'>
      <div className='row'>
        <div className='d-flex flex-wrap align-items-center mb-4'>
          <div className='flex-grow-1 d-flex'>
            <div>
            <p>Popular Apartment in Mysuru</p>
            </div>
            <div className='d-flex d-none d-md-inline ms-3'>
            <button
              className='btn btn-success text-dark px-3 py-0 me-2'
              onClick={() => setFilter('All')}
            >
              All
            </button>
            <button
              className='btn btn-success text-dark px-3 py-0 me-2'
              onClick={() => setFilter('2 BHK')}
            >
              2 BHK
            </button>
            <button
              className='btn btn-success text-dark px-3 py-0'
              onClick={() => setFilter('3 BHK')}
            >
              3 BHK
            </button>
          </div>


          </div>
          <p>s</p>
          <div>

          <FontAwesomeIcon icon={faSquareCaretRight} size="lg" style={{color: "#63E6BE",}} />
          </div>


        </div>

        {filteredData.map((item, index) => (
          <div className='col-12 col-sm-6 col-md-4 col-lg-3 mt-4' key={item.id}>
            <div className='card object-fit-fill border-white rounded-2 h-auto'>
              <img src={item.image} className='card-img-top rounded-4' alt={item.title} />
              {/* <button 
                className='btn' 
                style={{ color: item.liked ? 'red' : '#c2d8ff', position: 'absolute', top: '10px', right: '10px' }} 
                onClick={() => handleLike(item.id)}
              >
                <FontAwesomeIcon icon={faHeart} />
              </button> */}
              <div className='card-body'>
                <p className='card-title text-primary fw-bold fs-5'>{item.price}</p>
                <h6 className='card-title font '>{item.title}</h6>
                <p className='fw-light opacity-50'>{item.location}</p>
                <div className=' d-flex fw-lighter py-0 fs-7'>
                  <p className='me-3'>{item.sqft}</p>
                  <p className='me-3'>{item.wash}</p>
                  <p className='me-3'> {item.furnished}</p>
              </div>

              <div className='d-flex fw-lighter justify-content-between'>

                <div>
                Listed On 
                <p>{item.listed}</p>
                </div>
                <button type="button" class="btn btn-primary me-4 px-1 py-0">Veiw Details</button>
              </div>


              </div>

            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Popular;
